mapper.update:
UPDATE BEDANDCUST SET
      BEDANDCUST.CUSTID = #{customerID},
      BEDANDCUST.BEDID = #{bedId},
      BEDANDCUST.STARTTIME = #{startTime}
WHERE BEDANDCUST.ID = #{id}

UPDATE BEDANDCUST SET
    BEDANDCUST.ENDTIME = NULL
WHERE BEDANDCUST.ID = #{id}

UPDATE BEDANDCUST SET
    BEDANDCUST.CUSTID = #{customerID},
    BEDANDCUST.BEDID = #{bedId},
    BEDANDCUST.STARTTIME = #{startTime},
    BEDANDCUST.ENDTIME = #{endTime}
WHERE BEDANDCUST.ID = #{id}
